using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.UI;public class Barf_Script : MonoBehaviour {    System.Random rnd;    // Use this for initialization    void Start()    {        rnd = new System.Random();        int Pos = rnd.Next(-5, 6);        int Dir = rnd.Next(-31,30);        transform.position = new Vector3(Pos, 6, 0);        transform.Rotate(0,0,Dir);    }    // Update is called once per frame    void Update()    {        transform.position = transform.position + transform.up * -0.1f;        if (transform.position.y < -6)        {            Destroy(gameObject);        }    }}