using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.UI;public class UI : MonoBehaviour {    public Button openShop;    public Canvas shop;    public Button closeShop;    public Coin coinScript;    public Button upgrade;    public Text upgradeResponse;    public Tutorial Tutorial;	// Use this for initialization	void Start () {        openShop.onClick.AddListener(OpenShop);        closeShop.onClick.AddListener(CloseShop);        upgrade.onClick.AddListener(UpgradeShop);                shop.enabled = false;    }    void OpenShop()    {        shop.enabled = true;        Tutorial.TutorialTextUpdate("shop");           }    void CloseShop()    {        shop.enabled = false;        Tutorial.TutorialTextUpdate("CloseShop");    }    void UpgradeShop()    {        bool success = coinScript.AttemptUpgrade();        if (success)        {            Tutorial.TutorialTextUpdate("ButtonUp");            upgradeResponse.text = "success";        }        else        {            upgradeResponse.text = "failed";        }    }}